INSERT INTO `b_iblock_section` (`ID`, `TIMESTAMP_X`, `MODIFIED_BY`, `DATE_CREATE`, `CREATED_BY`, `IBLOCK_ID`, `IBLOCK_SECTION_ID`, `ACTIVE`, `GLOBAL_ACTIVE`, `SORT`, `NAME`, `PICTURE`, `LEFT_MARGIN`, `RIGHT_MARGIN`, `DEPTH_LEVEL`, `DESCRIPTION`, `DESCRIPTION_TYPE`, `SEARCHABLE_CONTENT`, `CODE`, `XML_ID`, `TMP_ID`, `DETAIL_PICTURE`, `SOCNET_GROUP_ID`) VALUES
(1, '2024-06-28 11:10:35', 1, '2024-06-28 14:10:35', 1, 2, NULL, 'Y', 'Y', 50, 'Обувь', 21, 1, 6, 1, 'Сложно представить себе элементы гардероба современного человека, которые выбирались бы более тщательно и основательно, чем обувь. Это больше, чем просто стильный аксессуар: ботинки, сапоги, туфли и другие модели выполняют очень важную функцию – они отвечают за комфорт, а иногда и за здоровье ног.<br />\n					<br />\n				', 'html', 'ОБУВЬ\r\nСЛОЖНО ПРЕДСТАВИТЬ СЕБЕ ЭЛЕМЕНТЫ ГАРДЕРОБА \r\nСОВРЕМЕННОГО ЧЕЛОВЕКА, КОТОРЫЕ ВЫБИРАЛИСЬ БЫ БОЛЕЕ ТЩАТЕЛЬНО И ОСНОВАТЕЛЬНО, ЧЕМ ОБУВЬ. ЭТО БОЛЬШЕ, ЧЕМ ПРОСТО СТИЛЬНЫЙ АКСЕССУАР: БОТИНКИ, САПОГИ, ТУФЛИ И ДРУГИЕ МОДЕЛИ ВЫПОЛНЯЮТ ОЧЕНЬ ВАЖНУЮ ФУНКЦИЮ – ОНИ ОТВЕЧАЮТ ЗА КОМФОРТ, А ИНОГДА И ЗА ЗДОРОВЬЕ НОГ.', 'shoes', '19', NULL, NULL, NULL),
(2, '2024-06-28 11:10:35', 1, '2024-06-28 14:10:35', 1, 2, 1, 'Y', 'Y', 500, 'Пантолеты', NULL, 2, 3, 2, 'Женские пантолеты – обувь, которая только с виду кажется непрактичной и простой. На самом деле, эти легкие и изящные шлепанцы универсальны: современные девушки все чаще выбирают их не только в качестве пляжной обуви, но и как удобный вариант на каждый день. Конечно, такая обувь в рамках офисного образа вряд ли станет удачным решением, однако в остальном у представительниц прекрасного пола – абсолютная свобода выбора.<br />\n							<br />\n						', 'html', 'ПАНТОЛЕТЫ\r\nЖЕНСКИЕ ПАНТОЛЕТЫ – ОБУВЬ, КОТОРАЯ ТОЛЬКО \r\nС ВИДУ КАЖЕТСЯ НЕПРАКТИЧНОЙ И ПРОСТОЙ. НА САМОМ ДЕЛЕ, ЭТИ ЛЕГКИЕ И ИЗЯЩНЫЕ ШЛЕПАНЦЫ УНИВЕРСАЛЬНЫ: СОВРЕМЕННЫЕ ДЕВУШКИ ВСЕ ЧАЩЕ ВЫБИРАЮТ ИХ НЕ ТОЛЬКО В КАЧЕСТВЕ ПЛЯЖНОЙ ОБУВИ, НО И КАК УДОБНЫЙ ВАРИАНТ НА КАЖДЫЙ ДЕНЬ. КОНЕЧНО, ТАКАЯ ОБУВЬ В РАМКАХ ОФИСНОГО ОБРАЗА ВРЯД ЛИ СТАНЕТ УДАЧНЫМ РЕШЕНИЕМ, ОДНАКО В ОСТАЛЬНОМ У ПРЕДСТАВИТЕЛЬНИЦ ПРЕКРАСНОГО ПОЛА – АБСОЛЮТНАЯ СВОБОДА ВЫБОРА.', 'pantolety', '26', NULL, NULL, NULL),
(3, '2024-06-28 11:10:36', 1, '2024-06-28 14:10:36', 1, 2, 1, 'Y', 'Y', 500, 'Тапочки', 22, 4, 5, 2, 'Яркие, удобные женские и мужские тапочки: для домашнего уюта и полноценного отдыха. Эта обувь помогает расслабиться после тяжелого и напряженного дня.\n							</br></br>\n							Так приятно, придя домой, сменить тесные туфли или туфли на высоком каблуке на комфортные тапочки.\n							</br></br>', 'html', 'ТАПОЧКИ\r\nЯРКИЕ, УДОБНЫЕ ЖЕНСКИЕ И МУЖСКИЕ ТАПОЧКИ: \r\nДЛЯ ДОМАШНЕГО УЮТА И ПОЛНОЦЕННОГО ОТДЫХА. ЭТА ОБУВЬ ПОМОГАЕТ РАССЛАБИТЬСЯ ПОСЛЕ ТЯЖЕЛОГО И НАПРЯЖЕННОГО ДНЯ. ТАК ПРИЯТНО, ПРИДЯ ДОМОЙ, СМЕНИТЬ ТЕСНЫЕ ТУФЛИ ИЛИ ТУФЛИ НА ВЫСОКОМ КАБЛУКЕ НА КОМФОРТНЫЕ ТАПОЧКИ.', 'slippers', '32', NULL, NULL, NULL);
